public class day2_1 {
  public static void main(String[] args) {
    int[][] kódTábla=new int[3][3];
    int sor=1;
    int oszlop=1;
    String mozgások[]=new String[5];
    mozgások[0]="";
    mozgások[1]="";
    mozgások[2]="";
    mozgások[3]="";
    mozgások[4]="";
    for (int i = 0; i < 3; i++) {
      for (int j = 0; j < 3; j++) {
        kódTábla[i][j]=0;
      }
    }
    kódTábla[0][0]=1;
    kódTábla[0][1]=2;
    kódTábla[0][2]=3;
    kódTábla[1][0]=4;
    kódTábla[1][1]=5;
    kódTábla[1][2]=6;
    kódTábla[2][0]=7;
    kódTábla[2][1]=8;
    kódTábla[2][2]=9;
    for (int i = 0; i < 3; i++) {
      for (int j = 0; j < 3; j++) {
        System.out.print(kódTábla[i][j]+", ");
      }
      System.out.println("");
    }
    System.out.println("A kód:");
    for (int i = 0; i < mozgások.length; i++) {//kiválaszt egy-egy sztringet
      for (int j = 0; j < mozgások[i].length(); j++) {//végigmegy az aktuális sztringen
        switch(mozgások[i].substring(j, j+1)){
          case "U": if(sor>0)
            sor--;break;
          case "D": if(sor<2)
            sor++;break;
          case "R": if(oszlop<2)
            oszlop++;break;
          case "L": if(oszlop>0)
            oszlop--;break;
        }
      }
      System.out.print(kódTábla[sor][oszlop]);
    }
    System.out.println("");
  }
}
